public class SpecialCharEx {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println('\'');//single포테이션 하나 출력
		System.out.println("abc\t123\b456");//\b에 의해 3이 지워져야 하지만, 이클립스 오류로 지워지지 않음
		System.out.println('\n');//개행이 가능
		System.out.println("\"Hello\"");//Hello를 출력
		System.out.println("c:\\");//c:\을 출력
		
	}

}
